import React,{useState} from 'react';
import './navbar.css'
import logo from '../../images/logo.png'
import {Link, NavLink} from "react-router-dom";
const Navbar = () => {

    const [showDropdown, setShowDropdown] = useState(false);
    const [click, setClick] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);
    const onMouseEnter = () => {
        if (window.innerWidth < 960) {
            setShowDropdown(false);
        } else {
            setShowDropdown(true);
        }
    };
    const onMouseLeave = () => {
        if (window.innerWidth < 960) {
            setShowDropdown(false);
        } else {
            setShowDropdown(false);
        }
    };
    const onClick = () => {
        setShowDropdown(!showDropdown);

    };
    const [sctoled, setScroled] = useState(false);

    window.addEventListener('scroll', () => {
        const scrollPos = window.scrollY;
        if (scrollPos > 10) {
            setScroled(true)
        } else {
            setScroled(false)
        }
    });

    return (
        <>
            <header id="header">
                <nav className={`navbar navbar-expand-lg py-0  ${sctoled && "scrolled"}` } >
                    <div className="container">
                        <Link className="navbar-brand" to="./" onClick={closeMobileMenu}>
                            <img src={logo} alt="logo"/>
                        </Link>
                        <button className="navbar-toggler d-lg-none" onClick={handleClick}>
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className={click ? 'collapse navbar-collapse' : 'navbar-collapse'} >
                            <ul className="navbar-nav ml-auto ">

                                <li className="nav-item">
                                    <NavLink  className="nav-link" activeClassName="active" exact to="/">home</NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className="nav-link" activeClassName="active" exact to="/afda"  onClick={closeMobileMenu}>features</NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className="nav-link" activeClassName="active" exact to='/asdfa'  onClick={closeMobileMenu}>team</NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className="nav-link" activeClassName="active" exact to='/afas'  onClick={closeMobileMenu}>testimonials</NavLink>
                                </li>

                                <li className="nav-item ">
                                    <NavLink className="nav-link" activeClassName="active" exact to="/asd"  onClick={closeMobileMenu}>pricing</NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className="nav-link" activeClassName="active" exact to="/contact"  onClick={closeMobileMenu}>contact</NavLink>
                                </li>


                            </ul>

                        </div>
                    </div>
                </nav>
            </header>
        </>
    );
};

export default Navbar;
